// Defer-init for small UX niceties; keep tiny
(()=>{"use strict";
document.addEventListener("DOMContentLoaded",()=>{
// Auto-apply ripple to all .btn and .linkish unless opted out
document.querySelectorAll(".btn, .linkish").forEach(el=>{
if(!el.hasAttribute("data-ripple-off")) el.setAttribute("data-ripple","");
});
// Respect hash focus for skip links
if(location.hash){ const el=document.querySelector(location.hash); el&&el.focus&&el.focus(); }
});
})();
