(()=>{"use strict";
const enabled=()=>!window.matchMedia||!window.matchMedia("(prefers-reduced-motion: reduce)").matches;
function ripple(e){
if(!enabled()) return;
const t=e.currentTarget, r=t.getBoundingClientRect();
const x=(e.clientX|| (r.left+r.width/2)) - r.left, y=(e.clientY|| (r.top+r.height/2)) - r.top;
const wave=document.createElement("span");
wave.className="ripple-wave";
const max=Math.max(r.width,r.height);
wave.style.width=wave.style.height=max+"px";
wave.style.left=(x - max/2)+"px";
wave.style.top=(y - max/2)+"px";
t.appendChild(wave);
wave.addEventListener("animationend",()=>wave.remove(),{once:true});
}
function bind(el){ if(!el._nrlcRippleBound){ el.addEventListener("click",ripple); el._nrlcRippleBound=true; } }
function scan(root=document){ root.querySelectorAll("[data-ripple]").forEach(bind); }
document.addEventListener("DOMContentLoaded",()=>{ scan(); });
window.NRLC_RIPPLE={bind,scan};
})();
